import React, { useState, useEffect } from "react";
import { Checkbox } from 'semantic-ui-react'

function Todo(){
    const [error , setError]= useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [todoList, setTodoList] = useState([]);

    const CheckboxExampleCheckbox = () => (
        <Checkbox label='Make my profile visible' />
      )
 
    useEffect(() => {
        fetch("/api/get-all")
        .then(res => res.json())
        .then(
            (result) => {
                setIsLoaded(true);
                setTodoList(result)
                console.log(result)
            },
            (error) => {
                setIsLoaded(true);
                setError(error);
            }
        )
    },[] )
    if(error){
        return <div> Error !!!</div>
    }else if(!isLoaded){
        return <div> Loading... </div>
    }else{
        return(
            
            <ul>
                {todoList.map(todo => (
                    <li>
                    <div class="ui checkbox">
                    <input type="checkbox" class="hidden" readonly={todo.done} tabindex={todo.done}/>
                    <label></label>
                    </div>

                    {todo.entry} {todo.date}{(todo.precedence)}


                    </li>
                ))}
            </ul>
        )
    }
}
export default Todo;
