import React, { useState, useEffect } from "react";
import Button from '@mui/material/Button';
import DeleteIcon from '@mui/icons-material/Delete';
import Stack from '@mui/material/Stack';
//import Chip from '@mui/material/Chip';


function Todo() {



    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [todoList, setTodoList] = useState([]);



    //delete 

    // const handleClick = () => {
    //     console.info('You clicked the Chip.');
    // };

    const handleDelete = (id) => {
        console.info('You clicked the delete icon.' + id);
        alert(id)
        fetch('http://localhost:8088/api/delete/' + id, {
            method: "DELETE"
        })
            .then(response => response.json())
            .then((result) => {
                console.log('Success:', result);
                getTodo();
            })
        //.then(data => this.useState({ todoList: data.todo }));
    };

    //true false 
    // const [checked, setChecked] = React.useState(true);
    // console.log("true false bas覺ld覺")
    // const handleChange = 
    // (event: React.ChangeEvent<HTMLInputElement>) => { setChecked(event.target.checked);   };


    useEffect(() => {
        getTodo()
    }, [])


    function getTodo() {
        fetch("/api/get-all")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setTodoList(result)
                    console.log(result)
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )

    }
    if (error) {
        return <div> Error !!!</div>
    } else if (!isLoaded) {
        return <div> Loading... </div>
    } else {
        return (

            <ul>
                {todoList.map(todo => (

                    <li>

                        {
                            // <Checkbox
                            //     checked={todo.done}

                            //      onChange={handleChange}
                            //      inputProps={{ 'aria-label': 'controlled' }}
                            // /> 
                        }

                        {todo.entry} {todo.date}{(todo.precedence)}
                        <Stack direction="row" spacing={2}>
                            <Button onClick={handleDelete} variant="outlined" startIcon={<DeleteIcon />}>
                                Delete
                            </Button>
                        </Stack>


                        {/*                         
                        <Stack direction="row" spacing={1}>
                            <Chip
                                label="Delete"
                                //onClick={handleClick}
                                onDelete={() => handleDelete(todo.id)}
                            />
                        </Stack> */}

                    </li>
                ))}
            </ul>
        )
    }
}
export default Todo;
