import React, { useState, useEffect } from "react";
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import EditIcon from '@mui/icons-material/Edit';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogTitle from '@mui/material/DialogTitle';
import AddIcon from '@mui/icons-material/Add';
import TextField from '@mui/material/TextField';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';

function Todo() {

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [todoList, setTodoList] = useState([]);
    const [willBeDeletedEntry, setWillBeDeletedEntry] = useState("");
    const [willDeletedId, setWillBeDeletedId] = useState(null);
    const [openDeleteDialog, setOpenDeleteDialog] = React.useState(false);
    const [openAddDialog, setOpenAddDialog] = React.useState(false);
    const [entry, setEntry] = useState("")
    const [date, setDate] = useState("")
    const [precedence, setPrecedence] = useState("")



    const handleUpdate = (id) => {

        console.log("edit iconuna tiklandı" + id);
    }

    const handleClickDelete = (entry, id) => {
        setOpenDeleteDialog(true);
        setWillBeDeletedEntry(entry);
        setWillBeDeletedId(id);
    };

    const handleCloseDelete = () => {
        setOpenDeleteDialog(false);
    };

    const handleDeleteConfirm = () => {

        console.info('You clicked the delete icon.' + willDeletedId);
        fetch('http://localhost:8088/api/delete/' + willDeletedId, {
            method: "DELETE"
        }).then((result) => {
            // result.json()
            // .then((resp) => {console.warn(resp) })
            handleCloseDelete();
            getTodo();
        })
    }

    const handleClickAdd = () => {
        setOpenAddDialog(true);
    };

    const handleCloseAdd = () =>{
        setOpenAddDialog(false)
    }

    const handleAddConfirm = () => {
          let todo = { entry, date, precedence}
        fetch('http://localhost:8088/api/create', {
            method: "POST",
            // headers: {
            //     'Accept': 'application/json',
            //     'Content-Type': 'application/json'
            // },
            // body: JSON.stringify(todo)
        })
            .then((result) => {
                result.json().then((resp) => {
                    console.warn(resp)
                    setOpenAddDialog(false);
                    getTodo();
                })

            })
    };

    // const handleGuncelle = (id) => {

    //     let todo = { entry, date, precedence, done }
    //     // let ent = document.querySelector(".entry"+id).value;
    //     // let setDate = document.querySelector(".date"+id).value;
    //     // let setPrecedence = document.querySelector(".precedence"+id).value;


    //     fetch('http://localhost:8088/api/update/' + id, {
    //         method: "PUT",
    //         headers: {
    //             'Accept': 'application/json',
    //             'Content-Type': 'application/json'
    //         },
    //         body: JSON.stringify(todo)
    //     })
    //         .then((result) => {
    //             result.json().then((resp) => {
    //                 console.warn(resp)
    //                 getTodo();
    //             })

    //         })

    // }


    // const handleDelete = (id) => {

    //     console.info('You clicked the delete icon.' + id);
    //     fetch('http://localhost:8088/api/delete/' + id, {
    //         method: "DELETE"
    //     }).then((result) => {
    //         // result.json()
    //         // .then((resp) => {console.warn(resp) })
    //         handleClose()
    //         getTodo()
    //     })
    // }


    // const handleAdd = (id) => {

    //     let gorev = document.querySelector(".add" + id);

    //     if (window.getComputedStyle(gorev).display == "none") {
    //         gorev.style.display = 'block';
    //     } else if (window.getComputedStyle(gorev).display == "block") {
    //         gorev.style.display = 'none';
    //     }
    // };

    // const handleCreate = (id) => {

    //     let todo = { entry, date, precedence, done }
    //     fetch('http://localhost:8088/api/create', {
    //         method: "POST",
    //         headers: {
    //             'Accept': 'application/json',
    //             'Content-Type': 'application/json'
    //         },
    //         body: JSON.stringify(todo)
    //     })
    //         .then((result) => {
    //             result.json().then((resp) => {
    //                 console.warn(resp)
    //                 getTodo();
    //             })

    //         })
    // };


    useEffect(() => {
        getTodo()
    }, [])


    function getTodo() {

        fetch("/api/get-all")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setTodoList(result)
                    console.log(result)
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }

            )
    }


    if (error) {
        return <div> Error !!!</div>
    } else if (!isLoaded) {
        return <div> Loading... </div>
    } else {
        return (
            <div>
                <div>
                    <Button variant="outlined" onClick={() => handleClickAdd()}><AddIcon></AddIcon></Button>
                </div>
                <div>
                    <Dialog open={openAddDialog} onClose={handleCloseAdd}>
                        <DialogTitle>Yeni Yapılacak Oluştur</DialogTitle>
                        <DialogContent>

                        <label>Yapilacak</label>    <input type="text" value={entry} onChange={(e) => setEntry(e.target.value)}/><br></br>
                        <label>Tarih</label>   <input type="date" value={date} onChange={(e) => setDate(e.target.value)} /><br></br>
                        <label>Öncelik</label>  <input type="text" value={precedence} onChange={(e) => setPrecedence(e.target.value)} />

                        </DialogContent>
                        <DialogActions>
                            <Button onClick={handleCloseAdd}>İptal</Button>
                            <Button onClick={handleAddConfirm}>Onayla</Button>
                        </DialogActions>
                    </Dialog>
                </div>
                <div>
                    <TableContainer component={Paper}>
                        <Table sx={{ minWidth: 650 }} aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                    <TableCell>Yapılacaklar</TableCell>
                                    <TableCell align="right">Tarih</TableCell>
                                    <TableCell align="right">Öncelik</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {todoList.map((row) => (
                                    <TableRow
                                        key={row.entry}
                                        sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                                    >
                                        <TableCell component="th" scope="row">
                                            {row.entry}
                                        </TableCell>
                                        <TableCell align="right">{row.date}</TableCell>
                                        <TableCell align="right">{row.precedence}</TableCell>
                                        <TableCell align="right"><EditIcon onClick={() => handleUpdate(row.id)} /></TableCell>
                                        <TableCell align="right"><DeleteForeverIcon onClick={() => handleClickDelete(row.entry, row.id)} /></TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </div>
                <div>
                    <Dialog
                        open={openDeleteDialog}
                        onClose={handleCloseDelete}
                        aria-labelledby="alert-dialog-title"
                    >
                        <DialogTitle id="alert-dialog-title">
                            {willBeDeletedEntry + " silinecek!"}
                        </DialogTitle>

                        <DialogActions>
                            <Button onClick={handleCloseDelete}>İptal</Button>
                            <Button onClick={handleDeleteConfirm} autoFocus>
                                Onayla
                            </Button>
                        </DialogActions>
                    </Dialog>
                </div>
            </div>
        )
    }

}
export default Todo;
