import React, { useState, useEffect } from "react";
import Checkbox from '@mui/material/Checkbox';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';

import DialogTitle from '@mui/material/DialogTitle';
import { DataGrid, GridColDef, GridValueGetterParams } from '@mui/x-data-grid';


function Todo() {



    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [todoList, setTodoList] = useState([]);
    const [id, setId] = useState();
    const [entry, setEntry] = useState("");
    const [date, setDate] = useState("");
    const [precedence, setPrecedence] = useState("");
    const [done, setDone] = useState("");
    //Delete dialog
    const [open, setOpen] = React.useState(false);

    const handleClickOpen = (id) => {
        console.log("delete tıklandi " + id)
        setOpen(true);
        setId(id)

    };

    const handleClose = () => {
        setOpen(false);
    };


    //true false 
    //const [checked, setChecked] = React.useState(true);

    const handleChange = (id) => {
        //console.log("true false tiklandi")

    }


    const handleUpdate = (id) => {

        let gorev = document.querySelector(".task" + id);

        if (window.getComputedStyle(gorev).display == "none") {
            gorev.style.display = 'block';
        } else if (window.getComputedStyle(gorev).display == "block") {
            gorev.style.display = 'none';
        }
    }

    const handleGuncelle = (id) => {

        let todo = { entry, date, precedence, done }
        // let ent = document.querySelector(".entry"+id).value;
        // let setDate = document.querySelector(".date"+id).value;
        // let setPrecedence = document.querySelector(".precedence"+id).value;


        fetch('http://localhost:8088/api/update/' + id, {
            method: "PUT",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(todo)
        })
            .then((result) => {
                result.json().then((resp) => {
                    console.warn(resp)
                    getTodo();
                })

            })

    }


    const handleDelete = (id) => {

        console.info('You clicked the delete icon.' + id);
        fetch('http://localhost:8088/api/delete/' + id, {
            method: "DELETE"
        }).then((result) => {
            // result.json()
            // .then((resp) => {console.warn(resp) })
            handleClose()
            getTodo()
        })
    }


    const handleAdd = (id) => {

        let gorev = document.querySelector(".add" + id);

        if (window.getComputedStyle(gorev).display == "none") {
            gorev.style.display = 'block';
        } else if (window.getComputedStyle(gorev).display == "block") {
            gorev.style.display = 'none';
        }
    };

    const handleCreate = (id) => {

        let todo = { entry, date, precedence, done }
        fetch('http://localhost:8088/api/create', {
            method: "POST",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(todo)
        })
            .then((result) => {
                result.json().then((resp) => {
                    console.warn(resp)
                    getTodo();
                })

            })
    };






    useEffect(() => {
        getTodo()
    }, [])


    function getTodo() {

        fetch("/api/get-all")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setTodoList(result)
                    console.log(result)
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }

            )
    }

    const columns = [
        { field: 'entry', headerName: 'Yapılacaklar', width: 130 },
        { field: 'date', headerName: 'Tarih', width: 130 },
        { field: 'precedence', headerName: 'Öncelik', type: 'number', width: 90},
        { field: 'edit', headerName: 'Güncelle', type: 'number', width: 90},
        { field: 'delete', headerName: 'Sil', type: 'number', width: 30}
    ];

    const rows = todoList;

    if (error) {
        return <div> Error !!!</div>
    } else if (!isLoaded) {
        return <div> Loading... </div>
    } else {
        return (
            <div>
                <div style={{ height: 400, width: '100%' }}>
                    <DataGrid
                        rows={rows}
                        columns={columns}
                        pageSize={5}
                        rowsPerPageOptions={[5]}
                        checkboxSelection
                    />
                </div>

  

            </div>
        )
    }

}
export default Todo;
