import React, { useState, useEffect } from "react";
import Checkbox from '@mui/material/Checkbox';
import { IconButton } from "@mui/material";
import Create from "./Create";
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';



function Todo() {



    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [todoList, setTodoList] = useState([]);

    const handleUpdate = () => {
        console.log('add tusuna tiklandi');
        <Box
        component="form"
        sx={{
          '& > :not(style)': { m: 1, width: '25ch' },
        }}
        noValidate
        autoComplete="off"
      >
        <TextField id="outlined-basic" label="Outlined" variant="outlined" />
        <TextField id="filled-basic" label="Filled" variant="filled" />
        <TextField id="standard-basic" label="Standard" variant="standard" />
      </Box>
        
    }


    //delete 

    // const handleClick = () => {
    //     console.info('You clicked the Chip.');
    // };

    const handleDelete = (id) => {
        console.info('You clicked the delete icon.' + id);
        fetch('http://localhost:8088/api/delete/' + id, {
            method: "DELETE"
        })
            .then(response => response.json())
            .then((result) => {
                console.log('Success:', result);
                getTodo();
            })

        //.then(data => this.useState({ todoList: data.todo }));
    };

    //true false 
    const [checked, setChecked] = React.useState(true);
    
    const handleChange =
        (event: React.ChangeEvent<HTMLInputElement>) => { setChecked(event.target.checked); };


    useEffect(() => {
        getTodo()
    }, [])


    function getTodo() {
        fetch("/api/get-all")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setTodoList(result)
                    console.log(result)
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }

            )
    }
    if (error) {
        return <div> Error !!!</div>
    } else if (!isLoaded) {
        return <div> Loading... </div>
    } else {
        return (
            <div>

            <ul>
                {todoList.map(todo => (

                    <li>

                        {
                            <Checkbox
                                checked={todo.done}

                                onChange={handleChange}
                                inputProps={{ 'aria-label': 'controlled' }}
                            />
                        }

                        {todo.entry} {todo.date}{(todo.precedence)}

                        <button onClick={() => handleUpdate()}> update </button>
                        <button onClick={() => handleDelete(todo.id)}> delete </button>

                        {/* <Chip
                                //onClick={onDelete}
                                onDelete={() => handleDelete(todo.id)}
                            />
                        */}

                    </li>
                    
                ))}
            </ul>
            </div>
        )
    }

}
export default Todo;
