import React, { useState, useEffect } from "react";
import Checkbox from '@mui/material/Checkbox';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { type } from "@testing-library/user-event/dist/type";
import { style } from "@mui/system";
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import { DataGrid, GridColDef, GridValueGetterParams } from '@mui/x-data-grid';
import DeleteIcon from '@mui/icons-material/Delete';
<svg data-testid="DeleteIcon"></svg>


function Todo() {



    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [todoList, setTodoList] = useState([]);
    const [id, setId] = useState();
    const [entry, setEntry] = useState("");
    const [date, setDate] = useState("");
    const [precedence, setPrecedence] = useState("");
    const [done, setDone] = useState("");
    //Delete dialog
    const [open, setOpen] = React.useState(false);

    const handleClickOpen = (id) => {
        console.log("delete tıklandi " + id)
        setOpen(true);
        setId(id)

    };

    const handleClose = () => {
        setOpen(false);
    };


    //true false 
    //const [checked, setChecked] = React.useState(true);

    const handleChange = (id) => {
        //console.log("true false tiklandi")

    }


    const handleUpdate = (id) => {

        let gorev = document.querySelector(".task" + id);

        if (window.getComputedStyle(gorev).display == "none") {
            gorev.style.display = 'block';
        } else if (window.getComputedStyle(gorev).display == "block") {
            gorev.style.display = 'none';
        }
    }

    const handleGuncelle = (id) => {

        let todo = { entry, date, precedence, done }
        // let ent = document.querySelector(".entry"+id).value;
        // let setDate = document.querySelector(".date"+id).value;
        // let setPrecedence = document.querySelector(".precedence"+id).value;


        fetch('http://localhost:8088/api/update/' + id, {
            method: "PUT",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(todo)
        })
            .then((result) => {
                result.json().then((resp) => {
                    console.warn(resp)
                    getTodo();
                })

            })

    }


    const handleDelete = (id) => {

        console.info('You clicked the delete icon.' + id);
        fetch('http://localhost:8088/api/delete/' + id, {
            method: "DELETE"
        }).then((result) => {
            // result.json()
            // .then((resp) => {console.warn(resp) })
            handleClose()
            getTodo()
        })
    }


    const handleAdd = (id) => {

        let gorev = document.querySelector(".add" + id);

        if (window.getComputedStyle(gorev).display == "none") {
            gorev.style.display = 'block';
        } else if (window.getComputedStyle(gorev).display == "block") {
            gorev.style.display = 'none';
        }
    };

    const handleCreate = (id) => {

        let todo = { entry, date, precedence, done }
        fetch('http://localhost:8088/api/create', {
            method: "POST",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(todo)
        })
            .then((result) => {
                result.json().then((resp) => {
                    console.warn(resp)
                    getTodo();
                })

            })
    };






    useEffect(() => {
        getTodo()
    }, [])


    function getTodo() {

        fetch("/api/get-all")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setTodoList(result)
                    console.log(result)
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }

            )
    }

    const columns: GridColDef[] = [
        { field: 'id', headerName: 'ID', width: 70 },
        { field: 'firstName', headerName: 'First name', width: 130 },
        { field: 'lastName', headerName: 'Last name', width: 130 },
        {
            field: 'age',
            headerName: 'Age',
            type: 'number',
            width: 90,
        },
        {
            field: 'fullName',
            headerName: 'Full name',
            description: 'This column has a value getter and is not sortable.',
            sortable: false,
        },
    ];

    const rows = [
        { id: 1, lastName: 'Snow', firstName: 'Jon', age: 35 },
        { id: 2, lastName: 'Lannister', firstName: 'Cersei', age: 42 },
        { id: 3, lastName: 'Lannister', firstName: 'Jaime', age: 45 },
        { id: 4, lastName: 'Stark', firstName: 'Arya', age: 16 },
        { id: 5, lastName: 'Targaryen', firstName: 'Daenerys', age: null },
        { id: 6, lastName: 'Melisandre', firstName: null, age: 150 },
        { id: 7, lastName: 'Clifford', firstName: 'Ferrara', age: 44 },
        { id: 8, lastName: 'Frances', firstName: 'Rossini', age: 36 },
        { id: 9, lastName: 'Roxie', firstName: 'Harvey', age: 65 },
    ];

    if (error) {
        return <div> Error !!!</div>
    } else if (!isLoaded) {
        return <div> Loading... </div>
    } else {
        return (
            <div>
                <div style={{ height: 400, width: '100%' }}>
                    <DataGrid
                        rows={rows}
                        columns={columns}
                        pageSize={5}
                        rowsPerPageOptions={[5]}
                        checkboxSelection
                    />
                </div>

                <>
                    {todoList.map(todo => (


                        <li>


                            <td>
                                {
                                    <Checkbox
                                        checked={todo.done}

                                        onChange={handleChange(todo.id)}
                                        inputProps={{ 'aria-label': 'controlled' }}
                                    />
                                }
                            </td>
                            <td> {todo.entry}</td>
                            <td> {todo.date}</td>
                            <td> {todo.precedence}</td>

                            <td>
                                <Button onClick={() => handleUpdate(todo.id)}> update </Button>
                                {/* <button onClick={() => handleDelete(todo.id)}> delete </button> */}

                                <div>
                                <svg data-testid="DeleteIcon"></svg>
                                    <Button variant="outlined" onClick={() => handleClickOpen(todo.id)}
                                        value={id} onChange={(e) => setId(e.target.value)}
                                    >
                                        delete
                                    </Button>
                                    <Dialog
                                        open={open}
                                        onClose={handleClose}
                                        aria-labelledby="alert-dialog-title"

                                    >
                                        <DialogTitle id="alert-dialog-title">
                                            {"Todo Silinecek eminmisiniz ?"}
                                        </DialogTitle>


                                        <DialogActions>
                                            <Button onClick={handleClose}>Disagree</Button>
                                            {/* <input  type="Number" value={id} onChange={(e)=>setId(e.target.value)}/> */}
                                            <Button onClick={() => handleDelete(id)}> Agree </Button>
                                        </DialogActions>
                                    </Dialog>
                                </div>

                            </td>

                            <div id="tasks" className={"task" + todo.id} >
                                {/* <input type="Number" defaultValue={todo.id} /> */}
                                <input type="text" value={entry} onChange={(e) => setEntry(e.target.value)} />
                                <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
                                <input type="Number" value={precedence} onChange={(e) => setPrecedence(e.target.value)} />
                                <input type="Boolean" value={done} onChange={(e) => setDone(e.target.value)} />
                                <button onClick={() => handleGuncelle(todo.id)}> güncelle </button>
                            </div>

                        </li>

                    )
                    )
                    }
                </>
                <div>
                    <button onClick={() => handleAdd(todoList.id)}>add</button>
                    <div id="add" className={"add" + todoList.id} >
                        {/* <input type="Number" defaultValue={todoList.id} /> */}
                        <input type="text" value={entry} onChange={(e) => setEntry(e.target.value)} />
                        <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
                        <input type="Number" value={precedence} onChange={(e) => setPrecedence(e.target.value)} />
                        <input type="Boolean" value={done} onChange={(e) => setDone(e.target.value)} />
                        <button onClick={() => handleCreate(todoList.id)}> create </button>
                    </div>
                </div>


            </div>
        )
    }

}
export default Todo;
