import React, { useState, useEffect } from "react";
import Checkbox from '@mui/material/Checkbox';
import { IconButton } from "@mui/material";
import Create from "./Create";



function Todo() {



    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [todoList, setTodoList] = useState([]);



    //delete 

    // const handleClick = () => {
    //     console.info('You clicked the Chip.');
    // };

    const handleDelete = (id) => {
        console.info('You clicked the delete icon.' + id);
        alert(id)
        fetch('http://localhost:8088/api/delete/' + id, {
            method: "DELETE"
        })
            .then(response => response.json())
            .then((result) => {
                console.log('Success:', result);
                getTodo();
            })

        //.then(data => this.useState({ todoList: data.todo }));
    };

    //true false 
    const [checked, setChecked] = React.useState(true);
    console.log("true false bas覺ld覺")
    const handleChange =
        (event: React.ChangeEvent<HTMLInputElement>) => { setChecked(event.target.checked); };


    useEffect(() => {
        getTodo()
    }, [])


    function getTodo() {
        fetch("/api/get-all")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setTodoList(result)
                    console.log(result)
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }

            )
    }
    if (error) {
        return <div> Error !!!</div>
    } else if (!isLoaded) {
        return <div> Loading... </div>
    } else {
        return (
            <div>
            <ul>
                {todoList.map(todo => (

                    <li>

                        {
                            <Checkbox
                                checked={todo.done}

                                onChange={handleChange}
                                inputProps={{ 'aria-label': 'controlled' }}
                            />
                        }

                        {todo.entry} {todo.date}{(todo.precedence)}
                        
                        <button onClick path = {'/src/components/Todo/Create.js'}>ADD</button>
                        
                        <button onClick={() => handleDelete(todo.id)}> delete </button>
                        {/* <Chip
                                //onClick={onDelete}
                                onDelete={() => handleDelete(todo.id)}
                            />
    */}

                    </li>
                ))}
            </ul>
            </div>
        )
    }

}
export default Todo;
