import React, { useState, useEffect } from "react";
import Checkbox from '@mui/material/Checkbox';
import { Icon, Label, Menu, Table } from 'semantic-ui-react'



function Todo() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [todoList, setTodoList] = useState([]);
    //true false 
    const [checked, setChecked] = React.useState(true);
    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setChecked(event.target.checked);
    };
    // delete



    useEffect(() => {
        fetch("/api/get-all")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setTodoList(result)
                    console.log(result)
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])
    if (error) {
        return <div> Error !!!</div>
    } else if (!isLoaded) {
        return <div> Loading... </div>
    } else {
        return (

            <ul>
                {todoList.map(todo => (
                    <li>
                        //
                        <Table celled>
    <Table.Header>
      <Table.Row>
        <Table.HeaderCell>tik</Table.HeaderCell>
        <Table.HeaderCell>entry</Table.HeaderCell>
        <Table.HeaderCell>date</Table.HeaderCell>
      </Table.Row>
    </Table.Header>

    <Table.Body>
      <Table.Row>
        <Table.Cell>
          <Label ribbon>{todo.done}</Label>
        </Table.Cell>
        <Table.Cell>{todo.entry}</Table.Cell>
        <Table.Cell>{todo.date}</Table.Cell>
      </Table.Row>
      <Table.Row>
        <Table.Cell>Cell</Table.Cell>
        <Table.Cell>Cell</Table.Cell>
        <Table.Cell>Cell</Table.Cell>
      </Table.Row>
      <Table.Row>
        <Table.Cell>Cell</Table.Cell>
        <Table.Cell>Cell</Table.Cell>
        <Table.Cell>Cell</Table.Cell>
      </Table.Row>
    </Table.Body>

    <Table.Footer>
      <Table.Row>
        <Table.HeaderCell colSpan='3'>
          <Menu floated='right' pagination>
            <Menu.Item as='a' icon>
              <Icon name='chevron left' />
            </Menu.Item>
            <Menu.Item as='a'>1</Menu.Item>
            <Menu.Item as='a'>2</Menu.Item>
            <Menu.Item as='a'>3</Menu.Item>
            <Menu.Item as='a'>4</Menu.Item>
            <Menu.Item as='a' icon>
              <Icon name='chevron right' />
            </Menu.Item>
          </Menu>
        </Table.HeaderCell>
      </Table.Row>
    </Table.Footer>
  </Table>
                        //
                        <Checkbox
                            checked={todo.done}
                            onChange={handleChange}
                            inputProps={{ 'aria-label': 'controlled' }}
                        />

                        {todo.entry} {todo.date}{(todo.precedence)}



                    </li>
                ))}
            </ul>
        )
    }
}
export default Todo;
