import React, { useState, useEffect } from "react";
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';

function Todo() {



    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [todoList, setTodoList] = useState([]);
    const [id, setId] = useState();
    const [entry, setEntry] = useState("");
    const [date, setDate] = useState("");
    const [precedence, setPrecedence] = useState("");
    const [done, setDone] = useState("");
    //Delete dialog
    const [open, setOpen] = React.useState(false);

    const handleClickOpen = (id) => {
        console.log("delete tÄ±klandi " + id)
        setOpen(true);
        setId(id)

    };

    const handleClose = () => {
        setOpen(false);
    };


    //true false 
    //const [checked, setChecked] = React.useState(true);

    const handleChange = (id) => {
        //console.log("true false tiklandi")

    }


    const handleUpdate = (id) => {

        let gorev = document.querySelector(".task" + id);

        if (window.getComputedStyle(gorev).display == "none") {
            gorev.style.display = 'block';
        } else if (window.getComputedStyle(gorev).display == "block") {
            gorev.style.display = 'none';
        }
    }

    const handleGuncelle = (id) => {

        let todo = { entry, date, precedence, done }
        // let ent = document.querySelector(".entry"+id).value;
        // let setDate = document.querySelector(".date"+id).value;
        // let setPrecedence = document.querySelector(".precedence"+id).value;


        fetch('http://localhost:8088/api/update/' + id, {
            method: "PUT",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(todo)
        })
            .then((result) => {
                result.json().then((resp) => {
                    console.warn(resp)
                    getTodo();
                })

            })

    }


    const handleDelete = (id) => {

        console.info('You clicked the delete icon.' + id);
        fetch('http://localhost:8088/api/delete/' + id, {
            method: "DELETE"
        }).then((result) => {
            // result.json()
            // .then((resp) => {console.warn(resp) })
            handleClose()
            getTodo()
        })
    }


    const handleAdd = (id) => {

        let gorev = document.querySelector(".add" + id);

        if (window.getComputedStyle(gorev).display == "none") {
            gorev.style.display = 'block';
        } else if (window.getComputedStyle(gorev).display == "block") {
            gorev.style.display = 'none';
        }
    };

    const handleCreate = (id) => {

        let todo = { entry, date, precedence, done }
        fetch('http://localhost:8088/api/create', {
            method: "POST",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(todo)
        })
            .then((result) => {
                result.json().then((resp) => {
                    console.warn(resp)
                    getTodo();
                })

            })
    };






    useEffect(() => {
        getTodo()
    }, [])


    function getTodo() {

        fetch("/api/get-all")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setTodoList(result)
                    console.log(result)
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }

            )
    }

    function createData(
        name: string,
        calories: number,
        fat: number,
        carbs: number,
        protein: number,
      ) {
        return { name, calories, fat, carbs, protein };
      }
      
      const rows = [
        createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
        createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
        createData('Eclair', 262, 16.0, 24, 6.0),
        createData('Cupcake', 305, 3.7, 67, 4.3),
        createData('Gingerbread', 356, 16.0, 49, 3.9),
      ];


    if (error) {
        return <div> Error !!!</div>
    } else if (!isLoaded) {
        return <div> Loading... </div>
    } else {
        return (
            <div>
                 <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
          
            <TableCell >YapÄ±lacaklar</TableCell>
            <TableCell align="right">Tarih</TableCell>
            <TableCell align="right">Ãncelik</TableCell>
   
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow
              key={row.name}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {row.name}
              </TableCell>
              <TableCell align="right">{row.calories}</TableCell>
              <TableCell align="right">{row.fat}</TableCell>
              <TableCell align="right">{row.carbs}</TableCell>
              <TableCell align="right">{row.protein}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>

  

            </div>
        )
    }

}
export default Todo;
