import React, { useState, useEffect } from "react";
import Checkbox from '@mui/material/Checkbox';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { type } from "@testing-library/user-event/dist/type";
import { style } from "@mui/system";



function Todo() {



    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [todoList, setTodoList] = useState([]);
    //const [id, setId] = useState();
    const [entry, setEntry] = useState("");
    const [date, setDate] = useState("");
    const [precedence, setPrecedence] = useState("");
    const [done, setDone] = useState("");


    //true false 
    //const [checked, setChecked] = React.useState(true);

    const handleChange = (id) => {
        console.log("true false tiklandi")
        
    }


    const handleUpdate = (id) => {

        let gorev = document.querySelector(".task" + id);

        if (window.getComputedStyle(gorev).display == "none") {
            gorev.style.display = 'block';
        } else if (window.getComputedStyle(gorev).display == "block") {
            gorev.style.display = 'none';
        }
    }

    const handleGuncelle = (id) => {

        let todo = { entry, date, precedence, done }
        // let ent = document.querySelector(".entry"+id).value;
        // let setDate = document.querySelector(".date"+id).value;
        // let setPrecedence = document.querySelector(".precedence"+id).value;


        fetch('http://localhost:8088/api/update/' + id, {
            method: "PUT",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(todo)
        })
            .then((result) => {
                result.json().then((resp) => {
                    console.warn(resp)
                    getTodo();
                })

            })

    }


    const handleDelete = (id) => {

        console.info('You clicked the delete icon.' + id);
        fetch('http://localhost:8088/api/delete/' + id, {
            method: "DELETE"
        }).then((result) => {
            result.json()
            //.then((resp) => {console.warn(resp) })
            getTodo()
        })
    }


    const handleAdd = (id) => {

        let gorev = document.querySelector(".add" + id);

        if (window.getComputedStyle(gorev).display == "none") {
            gorev.style.display = 'block';
        } else if (window.getComputedStyle(gorev).display == "block") {
            gorev.style.display = 'none';
        }
    };

    const handleCreate = (id) => {

        let todo = { entry, date, precedence, done }
        fetch('http://localhost:8088/api/create', {
            method: "POST",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(todo)
        })
            .then((result) => {
                result.json().then((resp) => {
                    console.warn(resp)
                    getTodo();
                })

            })
    };





    useEffect(() => {
        getTodo()
    }, [])


    function getTodo() {
        fetch("/api/get-all")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setTodoList(result)
                    console.log(result)
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }

            )
    }
    if (error) {
        return <div> Error !!!</div>
    } else if (!isLoaded) {
        return <div> Loading... </div>
    } else {
        return (
            <div>


                <ul>
                    {todoList.map(todo => (


                        <li>
                            <table>
                            <td>
                            {
                                <Checkbox 
                                    checked={todo.done}

                                    onChange={handleChange(todo.id)}
                                    inputProps={{ 'aria-label': 'controlled' }}
                                />
                            }
                            </td>
                            <td> {todo.entry}</td>
                            <td> {todo.date}</td>
                            <td> {todo.precedence}</td>
                            
                            <td>
                            <button onClick={() => handleUpdate(todo.id)}> update </button>
                            <button onClick={() => handleDelete(todo.id)}> delete </button>

                            </td>
                            </table>
                            <div id="tasks" className={"task" + todo.id} >
                                <input type="Number" defaultValue={todo.id} />
                                <input type="text" value={entry} onChange={(e) => setEntry(e.target.value)} />
                                <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
                                <input type="Number" value={precedence} onChange={(e) => setPrecedence(e.target.value)} />
                                <input type="Boolean" value={done} onChange={(e) => setDone(e.target.value)} />
                                <button onClick={() => handleGuncelle(todo.id)}> güncelle </button>
                            </div>





                            {/* <Chip
                                //onClick={onDelete}
                                onDelete={() => handleDelete(todo.id)}
                            />
                        */}

                        </li>

                    )
                    )
                    }
                </ul>
                <div>
                <button onClick={() => handleAdd(todoList.id)}>add</button>
                            <div id="add" className={"add" + todoList.id} >
                                {/* <input type="Number" defaultValue={todoList.id} /> */}
                                <input type="text" value={entry} onChange={(e) => setEntry(e.target.value)} />
                                <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
                                <input type="Number" value={precedence} onChange={(e) => setPrecedence(e.target.value)} />
                                <input type="Boolean" value={done} onChange={(e) => setDone(e.target.value)} />
                                <button onClick={() => handleCreate(todoList.id)}> create </button>
                            </div>
                </div>

            </div>
        )
    }

}
export default Todo;
