import React, { useState, useEffect } from "react";
import Checkbox from '@mui/material/Checkbox';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';



function Todo() {



    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [todoList, setTodoList] = useState([]);

    const handleGuncelle = (id) => {
        var id=id;
        var entry = document.querySelector("entry"+id);
        var date = document.querySelector("date"+id);
        var precedence = document.querySelector("precedence"+id);
console.log("buraya geldi:"+entry.value);
        // fetch('http://localhost:8088/api/update/' + id, {
        //     method: "PUT"
        // })
        //     .then(response => response.json())
        //     .then((result) => {
        //         console.log('Success:', result);
        //         getTodo();
        //     })
    }

    const handleUpdate = (id) => {
       
        var gorev = document.querySelector(".task"+id);
 
        if (window.getComputedStyle(gorev).display == "none") {
            gorev.style.display = 'block';
        } else if (window.getComputedStyle(gorev).display == "block") {
            gorev.style.display = 'none';
        }
    }



    //delete 

    // const handleClick = () => {
    //     console.info('You clicked the Chip.');
    // };

    const handleDelete = (id) => {
        console.info('You clicked the delete icon.' + id);
        fetch('http://localhost:8088/api/delete/' + id, {
            method: "DELETE"
        })
            .then(response => response.json())
            .then((result) => {
                console.log('Success:', result);
                getTodo();
            })

        //.then(data => this.useState({ todoList: data.todo }));
    };

    //true false 
    const [checked, setChecked] = React.useState(true);

    const handleChange =
        (event: React.ChangeEvent<HTMLInputElement>) => { setChecked(event.target.checked); };


    useEffect(() => {
        getTodo()
    }, [])


    function getTodo() {
        fetch("/api/get-all")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setTodoList(result)
                    console.log(result)
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }

            )
    }
    if (error) {
        return <div> Error !!!</div>
    } else if (!isLoaded) {
        return <div> Loading... </div>
    } else {
        return (
            <div>

                <ul>
                    {todoList.map(todo => (

                        <li>

                            {
                                <Checkbox
                                    checked={todo.done}

                                    onChange={handleChange}
                                    inputProps={{ 'aria-label': 'controlled' }}
                                />
                            }

                            {todo.entry} {todo.date}{(todo.precedence)}

                            <button onClick={() => handleUpdate(todo.id)}> update </button>
                            <button onClick={() => handleDelete(todo.id)}> delete </button>
                            <div id="tasks" class={"task"+todo.id} >
                            <input class={"entry"+todo.id} type="text" value={todo.entry}   /> 
                            <input class={"date"+todo.id} type="text" value={todo.date}    /> 
                            <input class={"precedence"+todo.id}  type="text" value={todo.precedence}   /> 
                            <button onClick={() => handleGuncelle(todo.id)}> güncelle </button>

                            </div>
                            {/* <Chip
                                //onClick={onDelete}
                                onDelete={() => handleDelete(todo.id)}
                            />
                        */}

                        </li>

                    ))}
                </ul>
            </div>
        )
    }

}
export default Todo;
